    return SingleChildScrollView(
      scrollDirection: Axis.horizontal,
      child: Container(
        child: ListView.builder(
          itemCount: _pokemonList.length,
          itemBuilder: (context, index) {
            final pokemon = _pokemonList[index];
              return Row(
                //_Cajita(pokemon: pokemon),
               // SizedBox.shrink(),
              );
            //---------------
          },
        ),
      ),
    );
   
  }


  Widget _Cajita({required TiposItem pokemon}){
    return InkWell(
      onTap: () {
        onSelectType(pokemon.tipo,);
        print((onSelectType));
      },
      child: Container(
        margin: EdgeInsets.symmetric(horizontal: 10,vertical: 10),
        child: Column(
          children: [
            SvgPicture.string(
              pokemon.svg ,
              
              width: 50, 
              height: 50,
              fit:BoxFit.fill,
            ),
            SizedBox(height: 5),
            Text(
              pokemon.tipo,
                style: TextStyle(
                color: Colors.white,
                fontSize: 20
              ),
            )
          ],
        ),
      ),
    );
  }
